Ôªø<script lang="ts">
  import type { Product as UIProduct } from '../../types/product';
  import ProductCard from '../cards/ProductCard.svelte';
  import PromotedListingsBanner from '../banners/home/PromotedListingsBanner.svelte';

  interface Props {
    promotedProducts: UIProduct[];
    onProductClick: (product: UIProduct) => void;
    onFavorite: (productId: string) => void;
    onBuy?: (productId: string, selectedSize?: string) => void;
    favoritesState: any;
    formatPrice: (price: number) => string;
    translations: {
      promoted_listings: string;
      promoted_description: string;
      common_currency: string;
      product_addToFavorites: string;
      seller_unknown: string;
      condition_brandNewWithTags: string;
      condition_newWithoutTags: string;
      condition_new: string;
      condition_likeNew: string;
      condition_good: string;
      condition_worn: string;
      condition_fair: string;
      categoryTranslation: (categoryName: string) => string;
    };
    showToggle?: boolean;
    activeTab?: 'sellers' | 'brands';
    onToggle?: (tab: 'sellers' | 'brands') => void;
    onViewAll?: () => void;
    class?: string;
  }

  let {
    promotedProducts,
    onProductClick,
    onFavorite,
    onBuy,
    favoritesState,
    formatPrice,
    translations,
    showToggle = false,
    activeTab = 'sellers',
    onToggle,
    onViewAll,
    class: className = ''
  }: Props = $props();

  // Use provided list (homepage already filters/chooses boosted); limit to 8
  const activePromotedProducts: UIProduct[] = $derived(
    (promotedProducts || []).slice(0, 8)
  );

  let promotedScrollContainer = $state<HTMLElement | null>(null);

  function scrollLeft() {
    if (promotedScrollContainer) {
      const cardWidth = promotedScrollContainer.querySelector('[data-promoted-card]')?.clientWidth || 280;
      promotedScrollContainer.scrollBy({ left: -cardWidth, behavior: 'smooth' });
    }
  }

  function scrollRight() {
    if (promotedScrollContainer) {
      const cardWidth = promotedScrollContainer.querySelector('[data-promoted-card]')?.clientWidth || 280;
      promotedScrollContainer.scrollBy({ left: cardWidth, behavior: 'smooth' });
    }
  }
</script>

{#if activePromotedProducts.length > 0}
	<!-- Enhanced section with separator banner -->
	<section class="pb-2 sm:pb-3 {className}">
		<!-- Section Banner with proper container -->
		<div class="px-2 sm:px-4 lg:px-6 mb-3 sm:mb-4">
			<PromotedListingsBanner
				heading={translations.promoted_listings}
				copy="Featured listings from top sellers. Premium visibility for standout products."
				itemCount={activePromotedProducts.length}
				showNavigation={false}
				{showToggle}
				{activeTab}
				{onToggle}
				cta={onViewAll ? { label: "View All", action: onViewAll } : undefined}
			/>
		</div>

		<!-- Horizontal Scrollable Cards matching grid card sizes -->
		<div class="relative px-2 sm:px-4 lg:px-6">
			<div class="flex gap-3 sm:gap-4 overflow-x-auto scrollbarhide promoted-cards-container" style="scroll-snap-type: x mandatory;" data-promoted-scroll bind:this={promotedScrollContainer}>
				{#each activePromotedProducts as product (product.id)}
					<div class="flex-shrink-0 snap-start basis-1/2 sm:basis-1/3 lg:basis-1/4 xl:basis-1/5" data-promoted-card>
						<ProductCard
							{product}
							onclick={onProductClick}
							onFavorite={onFavorite}
							favorited={favoritesState.favorites?.[product.id] || false}
							favoritesState={favoritesState}
							showBoostBadge={false}
							showSellerBadges={false}
							translations={{
								addToFavorites: translations.product_addToFavorites,
								currency: translations.common_currency,
								brandNewWithTags: translations.condition_brandNewWithTags,
								newWithoutTags: translations.condition_newWithoutTags,
								new: translations.condition_new,
								likeNew: translations.condition_likeNew,
								good: translations.condition_good,
								worn: translations.condition_worn,
								fair: translations.condition_fair,
								formatPrice: formatPrice,
								categoryTranslation: translations.categoryTranslation
							}}
						/>
					</div>
				{/each}
			</div>
		</div>
	</section>
{/if}

Ôªø<script lang="ts">
  import { isBrowser } from '../../utils/runtime.js';
  import ProductCard from '../cards/ProductCard.svelte';
  import ProductCardSkeleton from '../skeleton/ProductCardSkeleton.svelte';
  import type { Product } from '../../types/product';
  // Simple throttle implementation
  function throttle<T extends unknown[]>(fn: (...args: T) => void, limit: number) {
    let inThrottle: boolean;
    return function(...args: T) {
      if (!inThrottle) {
        fn(...args);
        inThrottle = true;
        setTimeout(() => inThrottle = false, limit);
      }
    };
  }


  interface Props {
    items: Product[];
    itemHeight?: number;
    containerHeight?: number;
    gap?: number;
    itemsPerRow?: number;
    onProductClick?: (product: Product) => void;
    onFavorite?: (product: Product) => void;
    class?: string;
    loading?: boolean;
    translations?: Record<string, any>;
    onEndReached?: () => void;
    endThreshold?: number;
    scrollParent?: 'self' | 'window';
    topOffset?: number;
    bottomOffset?: number;
  }

  let {
    items,
    itemHeight = 300,
    containerHeight = 600,
    gap = 16,
    itemsPerRow = 2, // Default for mobile
    onProductClick,
    onFavorite,
    class: className = '',
    loading = false,
    translations = {},
    onEndReached,
    endThreshold = 200,
    scrollParent = 'self',
    topOffset = 0,
    bottomOffset = 0
  }: Props = $props();

  let containerElement: HTMLDivElement;
  let sentinelElement = $state<HTMLDivElement>();
  let scrollTop = $state(0);
  let containerWidth = $state(0);
  let mounted = $state(false);
  let viewportHeightState = $state(containerHeight);
  
  // Pre-DOM measurement state
  let previousScrollHeight = $state(0);
  let previousViewportHeight = $state(0);
  let previousContainerWidth = $state(0);
  
  // Simple performance monitoring (optional)
  type PerformanceTimers = {
    startTiming: (label: string) => void;
    endTiming: (label: string) => void;
  };

  const perf: PerformanceTimers | null = null;

  // Calculate responsive items per row
  const responsiveItemsPerRow = $derived(() => {
    if (!mounted) return itemsPerRow;
    
    const width = containerWidth || (isBrowser ? window.innerWidth : 768);
    if (width >= 1280) return 5; // xl
    if (width >= 1024) return 4; // lg
    if (width >= 768) return 3;  // md
    if (width >= 640) return 2;  // sm
    return 2; // mobile
  });

  // Calculate visible range
  const visibleRange = $derived(() => {
    const itemsInRow = responsiveItemsPerRow();
    const totalRows = Math.ceil(items.length / itemsInRow);
    const rowHeight = itemHeight + gap;
    const vpHeight = scrollParent === 'self' ? containerHeight : viewportHeightState;

    const startRow = Math.floor(scrollTop / rowHeight);
    const endRow = Math.min(
      totalRows - 1,
      startRow + Math.ceil(vpHeight / rowHeight) + 1 // Buffer
    );

    const startIndex = startRow * itemsInRow;
    const endIndex = Math.min(items.length - 1, (endRow + 1) * itemsInRow - 1);

    return {
      startIndex: Math.max(0, startIndex),
      endIndex: Math.max(0, endIndex),
      startRow,
      endRow,
      totalRows,
      rowHeight
    };
  });

  // Get visible items
  const visibleItems = $derived(() => {
    const { startIndex, endIndex } = visibleRange();
    return items.slice(startIndex, endIndex + 1).map((item, index) => ({
      ...item,
      virtualIndex: startIndex + index
    })) as (Product & { virtualIndex: number })[];
  });

  // Calculate total height
  const totalHeight = $derived(() => {
    const { totalRows, rowHeight } = visibleRange();
    return totalRows * rowHeight;
  });

  // Calculate offset for visible items
  const offsetY = $derived(() => {
    const { startRow, rowHeight } = visibleRange();
    return startRow * rowHeight;
  });

  // Throttled scroll handler for better performance
  const handleScroll = throttle((event: Event) => {
    if (scrollParent === 'self') {
      const target = event.target as HTMLDivElement;
      scrollTop = target.scrollTop;
    } else {
      updateScrollTopFromWindow();
    }
  }, 16); // ~60fps

  function handleResize() {
    if (containerElement) {
      containerWidth = containerElement.clientWidth;
    }
  }

  function updateScrollTopFromWindow() {
    if (!containerElement) return;
    const rect = containerElement.getBoundingClientRect();
    const viewportStart = topOffset; // px from viewport top where content is visible
    const y = (viewportStart - rect.top);
    // scrollTop within container space
    scrollTop = Math.max(0, y);
  }

  // $effect.pre() for DOM measurements before updates
  $effect.pre(() => {
    // Capture dimensions before any DOM changes from reactive updates
    if (containerElement) {
      previousScrollHeight = containerElement.scrollHeight;
      previousContainerWidth = containerElement.clientWidth;
    }
    
    // Capture viewport height before window resize effects
    if (isBrowser && scrollParent === 'window') {
      previousViewportHeight = window.innerHeight;
    }
  });

  // Use $effect for lifecycle management
  $effect(() => {
    if (!isBrowser || !containerElement) return;
    mounted = true;
    
    // Check if we need to preserve scroll position after layout changes
    if (previousScrollHeight > 0 && containerElement.scrollHeight !== previousScrollHeight) {
      // Maintain relative scroll position when content height changes
      const scrollRatio = scrollTop / previousScrollHeight;
      const newScrollTop = scrollRatio * containerElement.scrollHeight;
      if (Math.abs(newScrollTop - scrollTop) > 10) { // Only adjust if significant change
        requestAnimationFrame(() => {
          if (containerElement) {
            containerElement.scrollTop = newScrollTop;
            scrollTop = newScrollTop;
          }
        });
      }
    }
    
    handleResize();
    const resizeObserver = new ResizeObserver(throttle(handleResize, 100));
    resizeObserver.observe(containerElement);

    const onWindowResize = throttle(() => {
      if (scrollParent === 'window') {
        // Compute viewport height minus offsets
        viewportHeightState = Math.max(0, window.innerHeight - topOffset - bottomOffset);
        updateScrollTopFromWindow();
      }
    }, 16);


    if (scrollParent === 'window') {
      viewportHeightState = Math.max(0, window.innerHeight - topOffset - bottomOffset);
      updateScrollTopFromWindow();
      window.addEventListener('resize', onWindowResize);
    }

    return () => {
      resizeObserver.disconnect();
      if (scrollParent === 'window') {
        window.removeEventListener('resize', onWindowResize as any);
      }
    };
  });

  // IntersectionObserver for end-reached detection
  $effect(() => {
    if (!isBrowser || !sentinelElement || !onEndReached || loading) return;

    const observer = new IntersectionObserver(
      (entries) => {
        const entry = entries[0];
        if (entry?.isIntersecting) {
          onEndReached();
        }
      },
      {
        root: scrollParent === 'self' ? containerElement : null,
        rootMargin: `0px 0px ${endThreshold}px 0px`,
        threshold: 0
      }
    );

    observer.observe(sentinelElement);

    return () => {
      observer.disconnect();
    };
  });

  // Grid positioning for items
  function getItemPosition(index: number) {
    const itemsInRow = responsiveItemsPerRow();
    const row = Math.floor(index / itemsInRow);
    const col = index % itemsInRow;
    const itemWidth = (containerWidth - (itemsInRow - 1) * gap) / itemsInRow;
    
    return {
      x: col * (itemWidth + gap),
      y: row * (itemHeight + gap),
      width: itemWidth
    };
  }
</script>

{#snippet loadingState()}
  <div class="absolute inset-0 grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4 p-4">
    {#each Array(20) as _}
      <ProductCardSkeleton />
    {/each}
  </div>
{/snippet}

{#snippet visibleItemsGrid()}
  <div 
    class="absolute w-full"
    style="transform: translateY({offsetY}px);"
  >
    {#each visibleItems() as item (item.id)}
      {@render virtualizedProductItem(item)}
    {/each}
  </div>
{/snippet}

{#snippet virtualizedProductItem(item: Product & { virtualIndex: number })}
  {@const position = getItemPosition(item.virtualIndex % responsiveItemsPerRow())}
  <div
    class="absolute"
    style="
      left: {position.x}px;
      top: {(Math.floor(item.virtualIndex / responsiveItemsPerRow()) - visibleRange().startRow) * (itemHeight + gap)}px;
      width: {position.width}px;
      height: {itemHeight}px;
    "
  >
    <ProductCard
      product={item}
      onclick={() => onProductClick?.(item)}
      onFavorite={() => onFavorite?.(item)}
      {translations}
      class="h-full"
    />
  </div>
{/snippet}

{#snippet emptyState()}
  <div class="absolute inset-0 flex items-center justify-center">
