/**
 * Accessibility utilities for focus management, live regions, and keyboard navigation
 * Used across dialogs, modals, search dropdowns, and other interactive components
 */

/**
 * Creates a focus trap for modal dialogs and dropdowns
 * Traps tab navigation within the specified element
 * 
 * @param element - The element to trap focus within
 * @returns Cleanup function to remove the trap
 * 
 * @example
 * ```ts
 * // In a Svelte component
 * let dialogElement: HTMLElement;
 * let cleanupTrap: (() => void) | null = null;
 * 
 * function openDialog() {
 *   cleanupTrap = trapFocus(dialogElement);
 * }
 * 
 * function closeDialog() {
 *   cleanupTrap?.();
 * }
 * ```
 */
export function trapFocus(element: HTMLElement): () => void {
  const focusableElements = element.querySelectorAll(
    'a[href], button, textarea, input[type="text"], input[type="radio"], input[type="checkbox"], select, [tabindex]:not([tabindex="-1"])'
  );
  
  const firstFocusable = focusableElements[0] as HTMLElement;
  const lastFocusable = focusableElements[focusableElements.length - 1] as HTMLElement;
  
  function handleTabKey(event: KeyboardEvent) {
    if (event.key !== 'Tab') return;
    
    if (event.shiftKey) {
      // Shift + Tab (backwards)
      if (document.activeElement === firstFocusable) {
        lastFocusable.focus();
        event.preventDefault();
      }
    } else {
      // Tab (forwards)
      if (document.activeElement === lastFocusable) {
        firstFocusable.focus();
        event.preventDefault();
      }
    }
  }
  
  // Focus first element immediately
  firstFocusable?.focus();
  
  // Add event listener
  document.addEventListener('keydown', handleTabKey);
  
  // Return cleanup function
  return () => {
    document.removeEventListener('keydown', handleTabKey);
  };
}

/**
 * Returns focus to a previously focused element
 * Used when closing dialogs, dropdowns, or other overlays
 * 
 * @param previousElement - Element that should regain focus
 * 
 * @example
 * ```ts
 * // Store reference when opening modal
 * const previouslyFocused = document.activeElement as HTMLElement;
 * 
 * // Return focus when closing
 * returnFocus(previouslyFocused);
 * ```
 */
export function returnFocus(previousElement: HTMLElement | null): void {
  if (previousElement && typeof previousElement.focus === 'function') {
    // Use setTimeout to ensure modal is completely closed first
    setTimeout(() => {
      previousElement.focus();
    }, 10);
  }
}

/**
 * Announces content to screen readers using a live region
 * Useful for dynamic content changes, search results, form validation
 * 
 * @param message - Message to announce
 * @param priority - 'polite' (default) or 'assertive' for urgency
 * 
 * @example
 * ```ts
 * // Announce search results
 * announceToScreenReader('5 products found', 'polite');
 * 
 * // Announce errors assertively
 * announceToScreenReader('Form submission failed', 'assertive');
 * ```
 */
export function announceToScreenReader(message: string, priority: 'polite' | 'assertive' = 'polite'): void {
  const liveRegion = document.createElement('div');
  liveRegion.setAttribute('aria-live', priority);
  liveRegion.setAttribute('aria-atomic', 'true');
  liveRegion.className = 'sr-only';
  liveRegion.style.position = 'absolute';
  liveRegion.style.left = '-10000px';
  liveRegion.style.width = '1px';
  liveRegion.style.height = '1px';
  liveRegion.style.overflow = 'hidden';
  
  document.body.appendChild(liveRegion);
  
  // Set message after a brief delay to ensure screen readers pick it up
  setTimeout(() => {
    liveRegion.textContent = message;
  }, 10);
  
  // Clean up after announcement
  setTimeout(() => {
    document.body.removeChild(liveRegion);
  }, 1000);
}

/**
 * Generates a unique ID for accessibility relationships
 * Used for aria-describedby, aria-labelledby, aria-controls, etc.
 * 
 * @param prefix - Prefix for the ID (e.g., 'search', 'dialog', 'error')
 * @returns Unique ID string
 * 
 * @example
 * ```ts
 * const searchId = generateA11yId('search'); // 'search-a11y-1234'
 * const errorId = generateA11yId('error');   // 'error-a11y-5678'
 * ```
 */
export function generateA11yId(prefix: string): string {
  return `${prefix}-a11y-${Math.random().toString(36).substr(2, 9)}`;
}

/**
 * Checks if an element is visible to screen readers and users
 * Useful for determining if focus management should apply
 * 
 * @param element - Element to check
 * @returns Whether element is visible
 */
export function isElementVisible(element: HTMLElement): boolean {
  return !!(
    element.offsetWidth ||
    element.offsetHeight ||
    element.getClientRects().length
  );
}

/**
 * Manages scroll locking for modal dialogs and overlays
 * Prevents background scrolling while maintaining scroll position
 * 
 * @param lock - Whether to lock or unlock scroll
 * 
 * @example
 * ```ts
 * // Lock scroll when opening modal
 * lockScroll(true);
 * 
 * // Unlock scroll when closing modal
 * lockScroll(false);
 * ```
 */
let originalOverflow = '';
let originalPaddingRight = '';

export function lockScroll(lock: boolean): void {
  const body = document.body;
  
  if (lock) {
    // Calculate scrollbar width to prevent layout shift
    const scrollbarWidth = window.innerWidth - document.documentElement.clientWidth;
    
    // Store original values
    originalOverflow = body.style.overflow;
    originalPaddingRight = body.style.paddingRight;
    
    // Apply lock
    body.style.overflow = 'hidden';
    if (scrollbarWidth > 0) {
      body.style.paddingRight = `${scrollbarWidth}px`;
    }
  } else {
    // Restore original values
    body.style.overflow = originalOverflow;
    body.style.paddingRight = originalPaddingRight;
  }
}

/**
 * Keyboard navigation handler for listbox/menu patterns
 * Handles Arrow keys, Home, End, Enter, Escape
 * 
 * @param event - Keyboard event
 * @param options - Navigation options
 * @returns Whether the event was handled
 * 
 * @example
 * ```ts
 * function handleKeyDown(event: KeyboardEvent) {
 *   const handled = handleKeyboardNavigation(event, {
 *     items: menuItems,
 *     activeIndex: currentIndex,
 *     onMove: (newIndex) => setCurrentIndex(newIndex),
 *     onSelect: (index) => selectItem(index),
 *     onEscape: () => closeMenu()
 *   });
 *   
 *   if (handled) {
 *     event.preventDefault();
 *   }
 * }
 * ```
 */
export interface KeyboardNavigationOptions {
  items: any[];
  activeIndex: number;
  onMove: (newIndex: number) => void;
  onSelect: (index: number) => void;
  onEscape?: () => void;
  loop?: boolean; // Whether to loop from last to first item
}

export function handleKeyboardNavigation(
  event: KeyboardEvent,
  options: KeyboardNavigationOptions
): boolean {
  const { items, activeIndex, onMove, onSelect, onEscape, loop = true } = options;
  
  switch (event.key) {
    case 'ArrowDown':
      if (activeIndex < items.length - 1) {
        onMove(activeIndex + 1);
      } else if (loop) {
        onMove(0);
      }
      return true;
      
    case 'ArrowUp':
      if (activeIndex > 0) {
        onMove(activeIndex - 1);
      } else if (loop) {
        onMove(items.length - 1);
      }
      return true;
      
    case 'Home':
      onMove(0);
      return true;
      
    case 'End':
      onMove(items.length - 1);
      return true;
      
    case 'Enter':
    case ' ':
      onSelect(activeIndex);
      return true;
      
    case 'Escape':
      onEscape?.();
      return true;
      
    default:
      return false;
  }
}

/**
 * Form accessibility utilities for labels, error states, and ARIA relationships
 * Used in Input, Select, SearchBar, and other form components
 */

import { generateA11yId } from './a11y.js';

/**
 * Configuration for form field accessibility
 */
export interface FormFieldA11yConfig {
  /** Field identifier (used for ID generation) */
  fieldId?: string;
  /** Label text (required for accessibility) */
  label?: string;
  /** Whether label should be visually hidden but accessible to screen readers */
  labelVisuallyHidden?: boolean;
  /** Helper text to describe the field */
  helperText?: string;
  /** Error message to display when field is invalid */
  errorMessage?: string;
  /** Whether the field is required */
  required?: boolean;
  /** Whether the field is currently invalid */
  invalid?: boolean;
}

/**
 * Generated accessibility attributes for a form field
 */
export interface FormFieldA11yAttributes {
  /** ID for the input element */
  inputId: string;
  /** ID for the label element */
  labelId: string;
  /** ID for the helper text element (if present) */
  helperTextId?: string;
  /** ID for the error message element (if present) */
  errorId?: string;
  /** Space-separated list of IDs that describe the input */
  ariaDescribedBy?: string;
  /** Whether the input is marked as invalid */
  ariaInvalid?: boolean;
  /** Whether the input is required */
  ariaRequired?: boolean;
}

/**
 * Generates all accessibility attributes needed for a form field
 * Handles label relationships, error states, helper text, and required fields
 * 
 * @param config - Form field configuration
 * @returns Accessibility attributes to apply to form elements
 * 
 * @example
 * ```svelte
 * <script lang="ts">
 *   import { generateFormFieldA11y } from '@repo/ui/utils/form-a11y';
 *   
 *   let { 
 *     value = $bindable(), 
 *     label, 
 *     helperText, 
 *     errorMessage, 
 *     required = false,
 *     invalid = false 
 *   } = $props();
 *   
 *   const a11y = generateFormFieldA11y({
 *     label,
 *     helperText,
 *     errorMessage,
 *     required,
 *     invalid
 *   });
 * </script>
 * 
 * <label for={a11y.inputId} class:sr-only={labelVisuallyHidden}>
 *   {label}
 *   {#if required}<span aria-label="required">*</span>{/if}
 * </label>
 * 
 * <input
 *   id={a11y.inputId}
 *   bind:value
 *   aria-describedby={a11y.ariaDescribedBy}
 *   aria-invalid={a11y.ariaInvalid}
 *   aria-required={a11y.ariaRequired}
 * />
 * 
 * {#if helperText}
 *   <div id={a11y.helperTextId} class="text-sm text-gray-600">
 *     {helperText}
 *   </div>
 * {/if}
 * 
 * {#if invalid && errorMessage}
 *   <div id={a11y.errorId} class="text-sm text-red-600" role="alert">
 *     {errorMessage}
 *   </div>
 * {/if}
 * ```
 */
export function generateFormFieldA11y(config: FormFieldA11yConfig): FormFieldA11yAttributes {
  const baseId = config.fieldId || generateA11yId('field');
  
  const attributes: FormFieldA11yAttributes = {
    inputId: baseId,
    labelId: `${baseId}-label`
  };
  
  const describedByIds: string[] = [];
  
  // Add helper text ID if present
  if (config.helperText) {
    attributes.helperTextId = `${baseId}-helper`;
    describedByIds.push(attributes.helperTextId);
  }
  
  // Add error ID if field is invalid and has error message
  if (config.invalid && config.errorMessage) {
    attributes.errorId = `${baseId}-error`;
    describedByIds.push(attributes.errorId);
  }
  
  // Set aria-describedby if there are describing elements
  if (describedByIds.length > 0) {
    attributes.ariaDescribedBy = describedByIds.join(' ');
  }
  
  // Set validation attributes
  if (config.invalid) {
    attributes.ariaInvalid = true;
  }
  
  if (config.required) {
    attributes.ariaRequired = true;
  }
  
  return attributes;
}

/**
 * Validation state management for forms
 * Helps manage field validation states and error messages
 */
export interface FieldValidation {
  isValid: boolean;
  errorMessage?: string;
  touched?: boolean;
}

export type FormValidation<T extends Record<string, any>> = {
  [K in keyof T]: FieldValidation;
};

/**
 * Creates a validation helper for form fields
 * 
 * @param initialFields - Initial field names to track
 * @returns Validation state manager
 * 
 * @example
 * ```ts
 * const validation = createFormValidation(['email', 'password']);
 * 
 * // Set field as invalid
 * validation.setFieldError('email', 'Please enter a valid email address');
 * 
 * // Mark field as touched
 * validation.setFieldTouched('email', true);
 * 
 * // Check if form is valid
 * if (validation.isFormValid()) {
 *   // Submit form
 * }
 * ```
 */
export function createFormValidation<T extends Record<string, any>>(
  initialFields: (keyof T)[]
): {
  fields: FormValidation<T>;
  setFieldError: (field: keyof T, message: string) => void;
  clearFieldError: (field: keyof T) => void;
  setFieldTouched: (field: keyof T, touched: boolean) => void;
  isFormValid: () => boolean;
  getFieldProps: (field: keyof T) => { invalid: boolean; errorMessage?: string };
} {
  const fields = {} as FormValidation<T>;
  
  // Initialize fields
  for (const field of initialFields) {
    fields[field] = {
      isValid: true,
      touched: false
    };
  }
  
  return {
    fields,
    
    setFieldError(field: keyof T, message: string) {
      fields[field] = {
        ...fields[field],
        isValid: false,
        errorMessage: message
      };
    },
    
    clearFieldError(field: keyof T) {
      fields[field] = {
        ...fields[field],
        isValid: true,
        errorMessage: undefined
      };
    },
    
    setFieldTouched(field: keyof T, touched: boolean) {
      fields[field] = {
        ...fields[field],
        touched
      };
    },
    
    isFormValid(): boolean {
      return Object.values(fields).every(field => field.isValid);
    },
    
    getFieldProps(field: keyof T) {
      const fieldState = fields[field];
      return {
        invalid: !fieldState.isValid && (fieldState.touched ?? false),
        errorMessage: fieldState.errorMessage
      };
    }
  };
}

/**
 * Common ARIA live region for form validation announcements
 * Use this to announce validation errors or success messages
 * 
 * @param message - Message to announce
 * @param type - Type of announcement ('error', 'success', 'info')
 * 
 * @example
 * ```ts
 * // Announce form errors
 * announceFormMessage('Please fix the errors below', 'error');
 * 
 * // Announce successful submission
 * announceFormMessage('Form submitted successfully', 'success');
 * ```
 */
export function announceFormMessage(
  message: string, 
  type: 'error' | 'success' | 'info' = 'info'
): void {
  // Use assertive for errors, polite for others
  const priority = type === 'error' ? 'assertive' : 'polite';
  
  // Import announceToScreenReader dynamically to avoid circular imports
  const announce = async () => {
    const { announceToScreenReader } = await import('./a11y.js');
    announceToScreenReader(message, priority);
  };
  
  announce();
}

/**
 * Input validation patterns and messages
 */
export const ValidationPatterns = {
  email: {
    pattern: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
    message: 'Please enter a valid email address'
  },
  
  password: {
    pattern: /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d@$!%*?&]{8,}$/,
    message: 'Password must be at least 8 characters with uppercase, lowercase, and number'
  },
  
  phone: {
    // Optional +, optional parens around country code, flexible separators
    pattern: /^\+?\(?\+?\d{2,3}\)?[-\s.]?\d{3,4}[-\s.]?\d{3,6}$/,
    message: 'Please enter a valid phone number'
  },
  
  required: {
    pattern: /\S+/,
    message: 'This field is required'
  }
} as const;

/**
 * Validates a field value against common patterns
 * 
 * @param value - Value to validate
 * @param rules - Validation rules to apply
 * @returns Validation result
 * 
 * @example
 * ```ts
 * const emailResult = validateField(emailValue, ['required', 'email']);
 * if (!emailResult.isValid) {
 *   
 * }
 * ```
 */
export function validateField(
  value: string,
  rules: (keyof typeof ValidationPatterns)[]
): { isValid: boolean; errorMessage?: string } {
  const trimmedValue = value?.trim() || '';
  
  for (const rule of rules) {
    const validator = ValidationPatterns[rule];
    if (!validator.pattern.test(trimmedValue)) {
      return {
        isValid: false,
        errorMessage: validator.message
      };
    }
  }
  
  return { isValid: true };
}

/**
 * Lightweight Logging Utility
 *
 * Provides simple logging functionality without external dependencies.
 * Designed to work in any environment (browser, server, tests).
 */

export type LogLevel = 'debug' | 'info' | 'warn' | 'error';

export interface LogContext {
  [key: string]: any;
}

export interface Logger {
  debug(message: string, context?: LogContext): void;
  info(message: string, context?: LogContext): void;
  warn(message: string, context?: LogContext): void;
  error(message: string, context?: LogContext): void;
}

class SimpleLogger implements Logger {
  constructor(
    private prefix: string,
    private minLevel: LogLevel = 'info'
  ) {}

  private shouldLog(level: LogLevel): boolean {
    const levels: Record<LogLevel, number> = {
      debug: 0,
      info: 1,
      warn: 2,
      error: 3
    };

    return levels[level] >= levels[this.minLevel];
  }

  private formatMessage(level: LogLevel, message: string, context?: LogContext): string {
    const timestamp = new Date().toISOString();
    const prefix = this.prefix ? `[${this.prefix}]` : '';
    const contextStr = context ? ` ${JSON.stringify(context)}` : '';

    return `${timestamp} ${level.toUpperCase()} ${prefix} ${message}${contextStr}`;
  }

  debug(message: string, context?: LogContext): void {
    if (!this.shouldLog('debug')) return;

    if (typeof console !== 'undefined' && console.debug) {
      console.debug(this.formatMessage('debug', message, context));
    }
  }

  info(message: string, context?: LogContext): void {
    if (!this.shouldLog('info')) return;

    if (typeof console !== 'undefined' && console.info) {
      console.info(this.formatMessage('info', message, context));
    }
  }

  warn(message: string, context?: LogContext): void {
    if (!this.shouldLog('warn')) return;

    if (typeof console !== 'undefined' && console.warn) {
      const formattedMessage = this.formatMessage('warn', message);
      console.warn(formattedMessage, context || '');
    }
  }

  error(message: string, context?: LogContext): void {
    if (!this.shouldLog('error')) return;

    if (typeof console !== 'undefined' && console.error) {
      const formattedMessage = this.formatMessage('error', message);
      console.error(formattedMessage, context || '');
    }
  }
}

/**
 * Create a logger instance with optional prefix
 */
export function createLogger(prefix?: string, minLevel: LogLevel = 'info'): Logger {
  return new SimpleLogger(prefix || '', minLevel);
}

/**
 * Default logger for general use
 */
export const logger = createLogger('UI');

/**
 * No-op logger for testing or when logging should be disabled
 */
export const noopLogger: Logger = {
  debug: () => {},
  info: () => {},
  warn: () => {},
  error: () => {}
};

/**
 * Get appropriate logger based on environment
 */
export function getLogger(prefix?: string): Logger {
  // In test environments, use noop logger to avoid console spam
  if (typeof process !== 'undefined' && process.env?.NODE_ENV === 'test') {
    return noopLogger;
  }

  // In development, enable debug logging
  const isDev = typeof process !== 'undefined' && process.env?.NODE_ENV === 'development';
  const minLevel: LogLevel = isDev ? 'debug' : 'info';

  return createLogger(prefix, minLevel);
}

